smoothScroll.code:
import Scrollbar from 'smooth-scrollbar';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

class SmoothScrollManager {
  constructor(containerSelector = '#scrollbar') {
    this.container = document.querySelector(containerSelector);
    this.scrollbar = null;
    this.isDesktop = window.innerWidth > 1023;
  }

  init() {
    if (!this.isDesktop || !this.container) return;

    this.scrollbar = Scrollbar.init(this.container, {
      damping: 0.1,
      thumbMinSize: 20,
      renderByPixels: true,
      alwaysShowTracks: false,
      continuousScrolling: true
    });

    this.setupProxy();
  }

  setupProxy() {
    ScrollTrigger.scrollerProxy(this.container, {
      scrollTop: (value) => {
        if (arguments.length) {
          this.scrollbar.scrollTop = value;
        }
        return this.scrollbar.scrollTop;
      }
    });

    this.scrollbar.addListener(ScrollTrigger.update);

    ScrollTrigger.defaults({
      scroller: this.container
    });
  }

  scrollTo(x, y, duration = 1000) {
    if (this.scrollbar) {
      this.scrollbar.scrollTo(x, y, duration);
    }
  }

  destroy() {
    if (this.scrollbar) {
      this.scrollbar.destroy();
      this.scrollbar = null;
    }
  }
}

export default SmoothScrollManager;

