function debounce(func, wait) {
  let timeout;
  return function executedFunction(...args) {
    clearTimeout(timeout);
    timeout = setTimeout(() => func.apply(this, args), wait);
  };
}

function splitTextToLines(element, wrapWords = false) {
  if (!element.dataset.originalHtml) {
    element.dataset.originalHtml = element.innerHTML.trim();
  }

  element.innerHTML = element.innerHTML.trim()
    .replace(/^\s+|\s+$/gm, '')
    .replace(/(<span\b[^>]*>[\s\S]*?<\/span>)|(\S+)/gi, (match, preserved, word) => {
      return preserved ? `<span class="preserved">${preserved}</span>` : `<span class="word">${word}</span>`;
    });

  const spans = Array.from(element.querySelectorAll('span.word, span.preserved'));
  let currentTop = 0;
  let result = '';

  requestAnimationFrame(() => {
    spans.forEach((span, index) => {
      const top = span.offsetTop;
      let content;

      if (index !== 0 && top - currentTop > 5) {
        result += '</span></span> ';
      }

      if (index === 0 || top - currentTop > 5) {
        result += '<span class="anim-line-wrap"><span class="anim-line"> ';
        currentTop = top;
      }

      content = span.classList.contains('preserved')
        ? span.innerHTML
        : wrapWords
          ? `<span class="word">${span.textContent}</span>`
          : span.textContent;

      result += content + ' ';
    });

    result += '</span></span>';
    element.innerHTML = result.trim();
  });
}

function animateLines(element, options = {}) {
  const defaults = {
    type: 'reveal',
    stagger: 0.2,
    duration: 1,
    ease: 'power2.out',
    y: 100,
    scrollTrigger: null
  };

  const settings = { ...defaults, ...options };
  const lines = element.querySelectorAll('.anim-line');

  element.style.opacity = 1;

  if (settings.type === 'reveal') {
    gsap.fromTo(lines,
      { yPercent: settings.y },
      {
        yPercent: 0,
        duration: settings.duration,
        stagger: { each: settings.stagger },
        ease: settings.ease,
        scrollTrigger: settings.scrollTrigger
      }
    );
  } else if (settings.type === 'fade') {
    gsap.fromTo(lines,
      { y: 20, opacity: 0 },
      {
        y: 0,
        opacity: 1,
        duration: settings.duration,
        stagger: { amount: settings.stagger * lines.length },
        ease: settings.ease,
        scrollTrigger: settings.scrollTrigger
      }
    );
  }
}

function initTextAnimations() {
  document.querySelectorAll('[data-animate-text]').forEach(el => {
    const type = el.dataset.animateText;
    splitTextToLines(el, type === 'title');
  });

  const handleResize = debounce(() => {
    document.querySelectorAll('[data-animate-text]').forEach(el => {
      if (el.dataset.originalHtml) {
        el.innerHTML = el.dataset.originalHtml;
        splitTextToLines(el, el.dataset.animateText === 'title');
      }
    });
  }, 300);

  window.addEventListener('resize', handleResize);
}

export { splitTextToLines, animateLines, initTextAnimations };

