class CustomCursor {
  constructor() {
    this.cursor = document.querySelector('.cursor');
    this.cursorElements = {
      video: this.cursor?.querySelector('.cursor__video'),
      works: this.cursor?.querySelector('.cursor__works'),
      show: this.cursor?.querySelector('.cursor__show'),
      arrow: this.cursor?.querySelector('.cursor__arrow')
    };

    this.isDesktop = window.innerWidth > 1023;
    this.currentSpeed = 0;

    this.speeds = {
      default: 0,
      video: 0.5,
      works: 2
    };

    if (this.isDesktop && this.cursor) {
      this.init();
    }
  }

  init() {
    gsap.to(this.cursor, { opacity: 1 });
    document.addEventListener('mousemove', this.onMouseMove.bind(this));
    this.bindHoverEvents();
  }

  onMouseMove(e) {
    const isOnVideo = e.target.closest('.cursor-video');
    const isOnWorks = e.target.closest('.cursor-works');

    let targetSpeed = this.speeds.default;
    if (isOnVideo) targetSpeed = this.speeds.video;
    if (isOnWorks) targetSpeed = this.speeds.works;

    if (this.currentSpeed !== targetSpeed) {
      gsap.to(this, {
        currentSpeed: targetSpeed,
        duration: 2,
        onUpdate: () => this.moveCursor(e, this.currentSpeed)
      });
    } else {
      this.moveCursor(e, this.currentSpeed);
    }
  }

  moveCursor(e, speed) {
    gsap.to(this.cursor, {
      x: e.clientX,
      y: e.clientY,
      duration: speed
    });
  }

  setHover() {
    gsap.to(this.cursor, { width: '6rem', height: '6rem', duration: 0.3 });
  }

  setVideo() {
    gsap.to(this.cursor, { width: '8rem', height: '8rem', duration: 0.3 });
    gsap.to(this.cursorElements.video, { opacity: 1, duration: 0.3 });
    this.cursor.classList.add('cursor--icon');
  }

  setWorks() {
    gsap.to(this.cursor, {
      width: '50rem',
      height: '50rem',
      top: '-20rem',
      left: '-15rem',
      duration: 0.3
    });
    gsap.to(this.cursorElements.works, { opacity: 1, duration: 0.3 });
    this.cursor.classList.add('cursor--works');
  }

  setShow() {
    gsap.to(this.cursor, { width: '8rem', height: '8rem', duration: 0.3 });
    gsap.to(this.cursorElements.show, { opacity: 1, duration: 0.3 });
    gsap.to(this.cursorElements.show.querySelector('p'), { opacity: 1, duration: 0.3, delay: 0.1 });
    this.cursor.classList.add('cursor--show');
  }

  setArrow() {
    gsap.to(this.cursor, { width: '8rem', height: '8rem', duration: 0.3 });
    gsap.to(this.cursorElements.arrow, { opacity: 1, duration: 0.3 });
    this.cursor.classList.add('cursor--icon');
  }

  reset() {
    gsap.to(this.cursor, {
      width: '2.5rem',
      height: '2.5rem',
      top: '-1.25rem',
      left: '-1.25rem',
      duration: 0.3
    });

    Object.values(this.cursorElements).forEach(el => {
      if (el) gsap.to(el, { opacity: 0, duration: 0.3 });
    });

    this.cursor.classList.remove('cursor--icon', 'cursor--works', 'cursor--show');
  }

  bindHoverEvents() {
    const bindings = [
      { selector: '.cursor-hover', enter: 'setHover', leave: 'reset' },
      { selector: '.cursor-video', enter: 'setVideo', leave: 'reset' },
      { selector: '.cursor-works', enter: 'setWorks', leave: 'reset' },
      { selector: '.cursor-show', enter: 'setShow', leave: 'reset' },
      { selector: '.cursor-arrow', enter: 'setArrow', leave: 'reset' }
    ];

    bindings.forEach(({ selector, enter, leave }) => {
      document.querySelectorAll(selector).forEach(el => {
        el.addEventListener('mouseenter', () => this[enter]());
        el.addEventListener('mouseleave', () => this[leave]());
      });
    });
  }
}

export default CustomCursor;

