class HorizontalGallery {
  constructor(containerSelector = '.works__list') {
    this.container = document.querySelector(containerSelector);
    if (!this.container || !window.its_desktop) return;

    this.items = Array.from(this.container.querySelectorAll('.works__item'));
    this.scrollWidth = this.container.scrollWidth - this.container.clientWidth;
    this.gap = this.getGap();
    this.itemWidths = this.items.map(item => item.getBoundingClientRect().width);
    this.itemOffsets = this.calculateOffsets();

    this.init();
  }

  getGap() {
    const style = getComputedStyle(this.container);
    return parseFloat(style.gap || style.columnGap) || 0;
  }

  calculateOffsets() {
    return this.itemWidths.reduce((acc, width, i) => {
      if (i === 0) acc.push(0);
      else acc.push(acc[i - 1] + this.itemWidths[i - 1] + this.gap);
      return acc;
    }, []);
  }

  init() {
    this.mainAnimation = gsap.to(this.container, {
      scrollLeft: this.scrollWidth,
      ease: 'none',
      duration: this.scrollWidth
    });

    ScrollTrigger.create({
      animation: this.mainAnimation,
      trigger: this.container,
      start: 'top top',
      end: `+=${this.scrollWidth}px`,
      scrub: true,
      pin: true,
      anticipatePin: 1,
      invalidateOnRefresh: true,
      onEnter: () => this.onEnter(),
      onLeave: () => this.onLeave(),
      onEnterBack: () => this.onEnter(),
      onLeaveBack: () => this.onLeave()
    });

    this.setupParallax();
    this.setupHover();
  }

  setupParallax() {
    this.items.forEach((item, index) => {
      const img = item.querySelector('.works__item-img');
      const nextItem = this.items[index + 1];
      const nextImg = nextItem?.querySelector('.works__item-img');
      const isLast = index === this.items.length - 1;

      const startOffset = this.itemOffsets[index];
      const endOffset = this.itemOffsets[index + 1] || (startOffset + this.itemWidths[index]);

      if (!isLast) {
        gsap.fromTo(img,
          { xPercent: 0 },
          {
            xPercent: 20,
            ease: 'none',
            scrollTrigger: {
              containerAnimation: this.mainAnimation,
              trigger: item,
              start: `left+=${startOffset} left`,
              end: `left+=${endOffset} left`,
              scrub: true,
              invalidateOnRefresh: true
            }
          }
        );
      }

      if (nextImg && index < this.items.length - 2) {
        gsap.fromTo(nextImg,
          { xPercent: -20 },
          {
            xPercent: 0,
            ease: 'none',
            scrollTrigger: {
              containerAnimation: this.mainAnimation,
              trigger: nextItem,
              start: `left+=${startOffset} left`,
              end: `left+=${endOffset} left`,
              scrub: true,
              invalidateOnRefresh: true
            }
          }
        );
      }
    });
  }

  setupHover() {
    this.items.forEach(item => {
      item.addEventListener('mouseover', () => {
        const imageId = item.dataset.image;
        document.querySelector('.works__block--active')?.classList.remove('works__block--active');
        document.querySelector(`.works__block[data-image="${imageId}"]`)?.classList.add('works__block--active');
      });
    });

    this.setupTitleFollow();
  }

  setupTitleFollow() {
    const titles = document.querySelectorAll('.works__block-title');

    window.addEventListener('mousemove', (e) => {
      const progress = Math.max(0, Math.min(1, e.clientX / window.innerWidth));

      titles.forEach(title => {
        const span = title.querySelector('span:not(.works__block-title--plug)');
        if (!span) return;

        const titleRect = title.getBoundingClientRect();
        const spanWidth = span.offsetWidth;
        const maxMove = titleRect.width - spanWidth;

        gsap.to(span, {
          x: progress * maxMove,
          ease: 'power2.out',
          duration: 0.5,
          overwrite: 'auto'
        });
      });
    });
  }

  onEnter() {
    if (window.toggleHeaderVisibility) {
      window.toggleHeaderVisibility(0);
    }
  }

  onLeave() {
    if (window.toggleHeaderVisibility) {
      window.toggleHeaderVisibility(1);
    }
  }
}

export default HorizontalGallery;

