class Preloader {
  constructor() {
    this.preloader = document.querySelector('.preloader');
    if (!this.preloader) {
      window.PRELOADER_DELAY = 0;
      return;
    }

    window.PRELOADER_DELAY = 3800;
    this.numbersWrap = this.preloader.querySelector('.preloader__numbers-wrap > div');
    this.blinkTexts = this.preloader.querySelectorAll('.preloader__text-blick');

    this.init();
  }

  init() {
    this.startBlinkEffect();
    this.startCountAnimation();
  }

  startBlinkEffect() {
    const animate = () => {
      const fadeInDelays = [...this.blinkTexts].map(() => Math.random() * 0.75);
      const maxFadeIn = Math.max(...fadeInDelays) + 1.5;
      const fadeOutDelays = [...this.blinkTexts].map(() => Math.random() * 0.75);
      const fadeOutStart = maxFadeIn + 3.5;

      const tl = gsap.timeline({ onComplete: animate });

      this.blinkTexts.forEach((text, i) => {
        tl.fromTo(text,
          { opacity: 0 },
          { opacity: 1, duration: 1.5, delay: fadeInDelays[i], ease: 'power2.in' },
          0
        );

        tl.to(text,
          { opacity: 0, duration: 1.5, delay: fadeOutDelays[i], ease: 'power2.out' },
          fadeOutStart
        );
      });
    };

    animate();
  }

  startCountAnimation() {
    const tl = gsap.timeline();

    tl.fromTo('.preloader__numbers',
      { opacity: 0 },
      { opacity: 1, duration: 0.75, ease: 'power1.inOut', delay: 1 },
      0
    );

    tl.to(this.numbersWrap,
      { y: '-25%', duration: 0.75, ease: 'power1.inOut' },
      1.33
    );

    tl.to(this.numbersWrap,
      { y: '-50%', duration: 0.75, ease: 'power1.inOut' },
      2.66
    );

    tl.to(this.numbersWrap, {
      y: '-75%',
      duration: 0.75,
      ease: 'power1.inOut',
      onComplete: () => this.complete()
    }, 4);
  }

  complete() {
    gsap.to('.preloader__text, .preloader__numbers-wrap, .preloader__numbers-span', {
      opacity: 0,
      duration: 0.5
    });

    gsap.to(this.preloader, {
      opacity: 0,
      duration: 0.8
    });

    setTimeout(() => {
      this.preloader.remove();
      window.PRELOADER = false;

      if (window.showMainScreen) {
        window.showMainScreen();
      }
    }, 1000);
  }
}

export default Preloader;

