function initAboutSection() {
  if (!document.querySelector('.about') || !window.its_desktop) return;

  const tl = gsap.timeline({
    scrollTrigger: {
      trigger: '.about__gsap',
      start: 'center center',
      end: '+=1000px',
      scrub: true,
      pin: true,
      onEnter: () => {
        document.querySelector('section.about').style.pointerEvents = 'none';
      },
      onLeave: () => {
        document.querySelector('section.about').style.pointerEvents = 'auto';
      },
      onEnterBack: () => {
        document.querySelector('section.about').style.pointerEvents = 'none';
      },
      onLeaveBack: () => {
        document.querySelector('section.about').style.pointerEvents = 'auto';
      }
    }
  });

  tl.fromTo('.about .mission',
    { maskSize: '0%' },
    { maskSize: '1000%', duration: 1, ease: 'power1.in' },
    0
  );

  tl.fromTo('.about__items',
    { opacity: 1 },
    { opacity: 0, duration: 0.25, delay: 0.75 },
    0
  );

  tl.fromTo('.about .mission__text, .about .text-slider',
    { y: '110%' },
    {
      y: '0%',
      duration: 0.5,
      delay: 0.25,
      stagger: { amount: 0.5 }
    },
    0
  );
}

function initMainScreenAnimation() {
  if (!document.querySelector('.main-screen') || !window.its_desktop) return;

  const tl = gsap.timeline({
    scrollTrigger: {
      trigger: '.main-screen',
      start: 'bottom bottom',
      end: '+=700px',
      scrub: 1,
      pin: '.main-screen',
      pinSpacing: false
    }
  });

  tl.fromTo('.about',
    { clipPath: 'polygon(0 5%, 100% 5%, 100% 100%, 0 100%)' },
    { clipPath: 'polygon(0 0%, 100% 0%, 100% 100%, 0 100%)', delay: 0.3, duration: 0.7 },
    0
  );

  tl.fromTo('.about .simple-title, .about .features',
    { opacity: 0 },
    { opacity: 1, delay: 0.4, duration: 0.6 },
    0
  );

  tl.fromTo('.main-screen__bottom, .main-screen__position',
    { y: '0px', scale: 1, opacity: 1 },
    { y: '-30px', scale: 0.9, opacity: 0, duration: 0.4 },
    0
  );

  tl.fromTo('.main-screen__image',
    { scale: 1, y: '0px', filter: 'brightness(1) blur(0px)' },
    { filter: 'brightness(0.2) blur(10px)', scale: 1.1, duration: 0.8, y: '100px', ease: 'power2.in' },
    0
  );

  tl.fromTo('.main-screen__title, .main-screen__nav',
    { scale: 1 },
    { y: '+=30px', scale: 0.9, opacity: 0, duration: 0.25 },
    0
  );
}

function initSectionAnimations() {
  initAboutSection();
  initMainScreenAnimation();
}

export { initSectionAnimations, initAboutSection, initMainScreenAnimation };

