class FormValidator {
  constructor(formSelector = 'form.inputs') {
    this.forms = document.querySelectorAll(formSelector);

    this.blockedDomains = [
      'gmail.com', 'yahoo.com', 'yahoo.co.uk', 'outlook.com',
      'hotmail.com', 'live.com', 'aol.com', 'icloud.com',
      'msn.com', 'protonmail.com', 'mail.com', 'gmx.com',
      'zoho.com', 'inbox.com', 'fastmail.com', 'qq.com',
      '163.com', '126.com', 'naver.com', 'daum.net', 'hanmail.net'
    ];

    this.init();
  }

  init() {
    this.forms.forEach(form => {
      this.setupEmailValidation(form);
      this.setupPhoneValidation(form);
      this.setupRequiredValidation(form);
      this.setupSubmitValidation(form);
    });
  }

  setupEmailValidation(form) {
    const emailInputs = form.querySelectorAll('input[type="email"]');

    emailInputs.forEach(input => {
      input.addEventListener('input', () => {
        let value = input.value.replace(/[^a-zA-Z0-9@._-]/g, '');
        input.value = value;

        if (this.isValidEmail(value)) {
          input.classList.add('inputs__field--active');
          input.classList.remove('inputs__field--invalid');

          const domain = value.split('@')[1]?.toLowerCase();
          if (this.blockedDomains.includes(domain)) {
            input.setCustomValidity('기업 이메일만 사용 가능합니다');
            input.classList.add('inputs__field--invalid');
          } else {
            input.setCustomValidity('');
          }
        } else if (value) {
          input.classList.add('inputs__field--invalid');
        }
      });

      input.addEventListener('blur', () => {
        if (!input.value) {
          input.classList.remove('inputs__field--invalid', 'inputs__field--active');
        } else if (!this.isValidEmail(input.value)) {
          input.classList.add('inputs__field--invalid');
        }
      });
    });
  }

  isValidEmail(email) {
    if (!email) return false;
    return email.includes('@') &&
           email.lastIndexOf('@') < email.length - 2 &&
           email.includes('.') &&
           email.lastIndexOf('.') > email.lastIndexOf('@') + 1 &&
           email.lastIndexOf('.') < email.length - 1;
  }

  setupPhoneValidation(form) {
    const phoneInputs = form.querySelectorAll('input[type="tel"]');

    phoneInputs.forEach(input => {
      input.addEventListener('input', () => {
        if (input.inputmask?.isComplete()) {
          input.classList.remove('inputs__field--invalid');
        }

        if (input.value) {
          input.classList.add('inputs__field--active');
        } else {
          input.classList.remove('inputs__field--active');
        }
      });
    });
  }

  setupRequiredValidation(form) {
    const requiredFields = form.querySelectorAll('[required]');

    requiredFields.forEach(field => {
      field.addEventListener('input', () => {
        field.classList.remove('inputs__field--invalid');

        if (field.value) {
          field.classList.add('inputs__field--active');
        } else {
          field.classList.remove('inputs__field--active');
        }
      });
    });
  }

  setupSubmitValidation(form) {
    const submitBtn = form.querySelector('button[type="submit"]');
    if (!submitBtn) return;

    submitBtn.addEventListener('click', (e) => {
      const requiredFields = form.querySelectorAll('[required]');
      let isValid = true;

      requiredFields.forEach(field => {
        if (field.type === 'checkbox') {
          if (!field.checked) {
            field.classList.add('inputs__field--invalid');
            isValid = false;
          }
        } else if (!field.value) {
          field.classList.add('inputs__field--invalid');
          isValid = false;
        }
      });

      if (!isValid) {
        e.preventDefault();

        const firstError = form.querySelector('.inputs__field--invalid');
        if (firstError) {
          firstError.scrollIntoView({ behavior: 'smooth', block: 'center' });
          firstError.focus();
        }
      }
    });
  }
}

export default FormValidator;

