class MobileMenu {
  constructor() {
    this.burger = document.querySelector('.header__burger');
    this.menu = document.querySelector('.mobile-menu');
    this.closeButtons = document.querySelectorAll('.mobile-menu__close');

    if (!this.burger || !this.menu) return;

    this.timeline = this.createTimeline();
    this.bindEvents();
  }

  createTimeline() {
    const tl = gsap.timeline({ paused: true });

    tl.to('.mobile-menu', {
      height: '100%',
      display: 'flex',
      duration: 1,
      ease: 'power2.out'
    }, 0);

    tl.fromTo('.mobile-menu .links__item span',
      { opacity: 0, y: '100%' },
      {
        y: '0%',
        opacity: 1,
        duration: 1,
        delay: 0.5,
        stagger: { amount: 0.5 },
        ease: 'power2.out'
      },
      0
    );

    tl.fromTo([
      '.mobile-menu .features span',
      '.mobile-menu__socials-link',
      '.mobile-menu__email',
      '.mobile-menu__close'
    ],
      { opacity: 0 },
      {
        opacity: 1,
        duration: 1,
        delay: 1,
        stagger: { each: 0.15 }
      },
      0
    );

    tl.fromTo('.mobile-menu .simple-title span',
      { opacity: 0 },
      { opacity: 1, duration: 1.5, delay: 1 },
      0
    );

    return tl;
  }

  bindEvents() {
    this.burger.addEventListener('click', () => this.open());

    this.closeButtons.forEach(btn => {
      btn.addEventListener('click', () => this.close());
    });

    document.addEventListener('keydown', (e) => {
      if (e.key === 'Escape') this.close();
    });
  }

  open() {
    this.timeline.play();

    if (!window.its_desktop) {
      document.body.classList.add('scroll');
    }
  }

  close() {
    this.menu.classList.remove('mobile-menu--active');
    this.timeline.timeScale(1.75).reverse();

    if (!window.its_desktop) {
      document.body.classList.remove('scroll');
    }
  }
}

window.hideMenu = function() {
  const menuInstance = window.mobileMenuInstance;
  if (menuInstance) menuInstance.close();
};

export default MobileMenu;

