function setupDeviceDetection() {
  window.SCROLL_EL = 'html';
  window.LARGE_TABLET = 1023;
  window.its_desktop = window.innerWidth > window.LARGE_TABLET;

  if (!window.its_desktop) {
    setupMobileViewport();
  }

  window.addEventListener('resize', () => {
    const wasDesktop = window.its_desktop;
    window.its_desktop = window.innerWidth > window.LARGE_TABLET;

    if (wasDesktop !== window.its_desktop) {
      location.reload();
    }
  });
}

function setupMobileViewport() {
  const setVh = () => {
    const vh = window.innerHeight * 0.01;
    document.documentElement.style.setProperty('--vh', `${vh}px`);
  };

  setVh();
  document.addEventListener('DOMContentLoaded', setVh);
  window.addEventListener('load', setVh);
  window.addEventListener('resize', setVh);
}

function stopScrollMobile() {
  if (!document.body.classList.contains('scroll')) {
    document.body.classList.add('scroll');
  }
}

function startScrollMobile() {
  if (document.body.classList.contains('scroll')) {
    document.body.classList.remove('scroll');
  }
}

window.stopScrollMobile = stopScrollMobile;
window.startScrollMobile = startScrollMobile;

export { setupDeviceDetection, setupMobileViewport, stopScrollMobile, startScrollMobile };

