function initPopups() {
  const popups = document.querySelectorAll('.popup-wrap');

  popups.forEach(popup => {
    popup.addEventListener('click', (e) => {
      if (!e.target.closest('.popup')) {
        closePopup(popup.id);
      }
    });
  });
}

function showPopup(id) {
  const popup = document.getElementById(id);
  if (!popup) return;

  setTimeout(() => {
    const popupContent = popup.querySelector('.popup');
    if (popupContent.offsetHeight + 60 >= window.innerHeight) {
      popup.classList.add('popup-wrap--end');
    }
  }, 500);

  if (!window.its_desktop) {
    window.stopScrollMobile();
  }

  popup.classList.add('popup-wrap--active');

  requestAnimationFrame(() => {
    requestAnimationFrame(() => {
      popup.classList.add('popup-wrap--opacity1');
    });
  });

  popup.addEventListener('transitionend', function handler() {
    popup.querySelector('.popup').classList.add('popup--opacity1');
    popup.removeEventListener('transitionend', handler);
  });
}

function closePopup(id) {
  const popup = document.getElementById(id);
  if (!popup) return;

  popup.classList.remove('popup-wrap--end');

  if (!window.its_desktop) {
    window.startScrollMobile();
  }

  const video = popup.querySelector('video');
  if (video) {
    video.pause();
    video.currentTime = 0;
  }

  const popupContent = popup.querySelector('.popup');
  popupContent.classList.remove('popup--opacity1');

  popupContent.addEventListener('transitionend', function handler() {
    popup.classList.remove('popup-wrap--opacity1');
    setTimeout(() => {
      popup.classList.remove('popup-wrap--active');
    }, 400);
    popupContent.removeEventListener('transitionend', handler);
  });
}

window.show_popup = showPopup;
window.close_popup = closePopup;

export { initPopups, showPopup, closePopup };

