function initAnchors() {
  if (window.location.hash && window.location.hash.startsWith('#anchor')) {
    const targetId = window.location.hash.replace('#anchor', '');
    if (targetId) {
      scrollToAnchor(targetId, true);
    }
  }

  document.querySelectorAll('.anchors a:not([data-anchors]), .single-anchors').forEach(link => {
    link.addEventListener('click', (e) => {
      if (link.hasAttribute('data-mobile') && window.its_desktop) return;

      e.preventDefault();
      let href = link.getAttribute('href');

      if (!href.includes('#')) {
        scrollToAnchor(href);
      } else {
        const basePath = href.substring(0, href.indexOf('#'));
        const targetId = href.substring(href.indexOf('#') + 1);
        const newHref = basePath + '#anchor' + targetId;

        if (document.getElementById(targetId)) {
          scrollToAnchor(targetId);
        } else {
          setTimeout(() => {
            location.href = newHref;
          }, 250);
        }
      }
    });
  });
}

function scrollToAnchor(targetId, isInitial = false) {
  const target = document.getElementById(targetId);
  if (!target) return;

  if (window.its_desktop && window.scrollbar) {
    if (isInitial) window.scrollbar._init();

    const targetTop = target.getBoundingClientRect().top + window.scrollbar.scrollTop;
    const offset = target.offsetHeight < window.innerHeight
      ? (window.innerHeight - target.offsetHeight) / 2
      : parseInt(target.dataset.anchorOffset) || 50;

    window.scrollbar.scrollTo(0, targetTop - offset, 1500);
  } else {
    const targetTop = target.getBoundingClientRect().top + window.scrollY;
    window.scrollTo({
      top: targetTop,
      behavior: 'smooth'
    });
  }

  if (window.hideMenu) {
    window.hideMenu();
  }

  history.replaceState(null, '', window.location.pathname + window.location.search);
}

export { initAnchors, scrollToAnchor };

