import Scrollbar from 'smooth-scrollbar';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

import SmoothScrollManager from './core/smoothScroll.js';
import { setupDeviceDetection } from './core/deviceDetect.js';

import Preloader from './animations/preloader.js';
import { initTextAnimations, splitTextToLines, animateLines } from './animations/textAnimations.js';
import { initSectionAnimations } from './animations/sectionAnimations.js';

import CustomCursor from './interactions/customCursor.js';
import FormValidator from './interactions/formValidation.js';
import initTiltEffect from './interactions/vanillaTilt.js';

import HorizontalGallery from './components/horizontalGallery.js';
import MobileMenu from './components/mobileMenu.js';
import { initPopups } from './components/popup.js';
import { initAnchors } from './components/anchors.js';

gsap.registerPlugin(ScrollTrigger);

class App {
  constructor() {
    window.SCROLL_EL = 'html';
    window.LARGE_TABLET = 1023;
    window.its_desktop = window.innerWidth > window.LARGE_TABLET;
    window.PRELOADER_DELAY = 0;

    this.init();
  }

  init() {
    if (document.readyState === 'loading') {
      document.addEventListener('DOMContentLoaded', () => this.onDOMReady());
    } else {
      this.onDOMReady();
    }
  }

  onDOMReady() {
    new Preloader();
    setupDeviceDetection();

    if (window.its_desktop) {
      this.smoothScroll = new SmoothScrollManager('#scrollbar');
      this.smoothScroll.init();
      window.scrollbar = this.smoothScroll.scrollbar;
    }

    window.addEventListener('load', () => this.onLoad());
  }

  onLoad() {
    ScrollTrigger.refresh();

    this.initAnimations();
    this.initInteractions();
    this.initComponents();

    setTimeout(() => ScrollTrigger.refresh(), 2000);
  }

  initAnimations() {
    initTextAnimations();
    initSectionAnimations();
    this.initMainScreen();
  }

  initInteractions() {
    if (window.its_desktop) {
      new CustomCursor();
    }

    new FormValidator();
    initTiltEffect();
  }

  initComponents() {
    new HorizontalGallery();
    window.mobileMenuInstance = new MobileMenu();
    initPopups();
    initAnchors();
  }

  initMainScreen() {
    if (!document.querySelector('.main-screen')) return;

    window.showMainScreen = () => {
      const tl = gsap.timeline();

      tl.fromTo('.main-screen__nav-item, .header__logo, .header__burger',
        { opacity: 0 },
        {
          opacity: 1,
          duration: 1.25,
          delay: () => Math.random() * 1.75,
          ease: 'power2.in'
        }
      );

      tl.to('.main-screen__image', {
        x: -6,
        duration: 6
      }, 0);
    };

    if (window.PRELOADER_DELAY === 0) {
      window.showMainScreen();
    }
  }
}

new App();

window.toggleHeaderVisibility = (opacity) => {
  gsap.to('.header__wrap', { opacity, duration: 0.3 });
  document.querySelector('.header').style.pointerEvents = opacity === 1 ? 'auto' : 'none';
};

