$breakpoint-tablet: 1023px;
$transition-default: 0.3s ease;

@mixin desktop {
  @media (min-width: #{$breakpoint-tablet + 1px}) {
    @content;
  }
}

@mixin tablet {
  @media (max-width: #{$breakpoint-tablet}) {
    @content;
  }
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html, body {
  overflow: hidden;

  @include tablet {
    overflow: auto;
    overflow-x: hidden;
  }
}

body.scroll {
  overflow: hidden;
  position: fixed;
  width: 100%;
}

#scrollbar {
  height: 100vh;

  @include tablet {
    height: auto;
  }
}

.anim-line-wrap {
  overflow: hidden;
  display: block;
}

.anim-line {
  display: block;
  will-change: transform;
}

[data-animate-text] {
  opacity: 0;
}

.cursor {
  position: fixed;
  top: -1.25rem;
  left: -1.25rem;
  width: 2.5rem;
  height: 2.5rem;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.8);
  pointer-events: none;
  z-index: 9999;
  mix-blend-mode: difference;
  opacity: 0;
  will-change: transform, width, height;
}

.cursor__video,
.cursor__works,
.cursor__show,
.cursor__arrow {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  opacity: 0;
  pointer-events: none;
}

.cursor--icon {
  background: rgba(0, 0, 0, 0.8);
  mix-blend-mode: normal;
}

.cursor--works {
  background: transparent;
  border: 1px solid rgba(255, 255, 255, 0.3);
}

.tilt-start {
  transform-style: preserve-3d;
  will-change: transform;
}

.tilt-start .js-tilt-glare {
  border-radius: inherit;
}

.inputs__field {
  border-bottom: 1px solid rgba(255, 255, 255, 0.3);
  transition: border-color 0.3s;
}

.inputs__field--active {
  border-color: rgba(255, 255, 255, 0.8);
}

.inputs__field--invalid {
  border-color: #ff4444;
}

.inputs__field--invalid::placeholder {
  color: #ff4444;
}

.about .mission {
  position: absolute;
  inset: 0;
  background: #000;
  -webkit-mask-image: radial-gradient(circle, black 50%, transparent 50%);
  mask-image: radial-gradient(circle, black 50%, transparent 50%);
  -webkit-mask-size: 0%;
  mask-size: 0%;
  -webkit-mask-position: center;
  mask-position: center;
  -webkit-mask-repeat: no-repeat;
  mask-repeat: no-repeat;
}

.works__list {
  display: flex;
  gap: 2rem;
  overflow-x: hidden;
  will-change: scroll-position;
}

.works__item {
  flex-shrink: 0;
  width: 40vw;
}

.works__item-img {
  overflow: hidden;
  will-change: transform;
}

.works__item-img img {
  width: 120%;
  will-change: transform;
}

.works__block {
  position: fixed;
  inset: 0;
  opacity: 0;
  transition: opacity 0.5s;
}

.works__block--active {
  opacity: 1;
}
